const colors = require('colors');


/*
|------------------------------------------------------------------------------
| Console Attention
|------------------------------------------------------------------------------
|
| Hackers can send to the client a code told him to copy-paste something in the
| console to enable a website feature or "hack" someone's account.
|
*/


/**
 * Handle an incoming request
 *
 * @param req
 * @param res
 * @param next
 * @return void
 */

 module.exports = function (req , res ,next) {

    console.log(colors.red.bold.underline(`STOP ! `));

    console.log(colors.white.bold(`This is a browser feature intended for developers.
         If someone told you to copy-paste something here to enable a Facebook feature 
         or "hack" someone's account, it is a scam and will give them access to your 
         account.`));

    next();

};
